/*
Add SQL statements here that you want to run before
the test script is run.

In the lower pane, you can add test conditions that verify 
whether the results of your SQL statements match what 
you expect.
*/
DECLARE @EmpCode		CHAR(5),
		@FirstName		VARCHAR(50) = 'Test',
		@LastName		VARCHAR(50) = 'Test',
		@Parent			INT			= 1,
		@Address1		VARCHAR(50) = 'Sample Address',
		@StoreId		INT			= 2,
		@Blood			CHAR(3)		= 'B+',
		@IsMan			BIT			= 1,
		@Designation	VARCHAR(10) = 'Manager',
		@parenthier		hierarchyid,
		@newHier		hierarchyid,
		@maxHier		hierarchyid

SELECT @parenthier = OrgPath
FROM Stores.Employee
WHERE EmployeeId = @Parent

SELECT @maxHier = NULLIF(MAX(OrgPath), @parenthier)
FROM Stores.Employee
WHERE OrgPath.IsDescendantOf(@parenthier) = 1

SELECT @newHier = @parenthier.GetDescendant(@maxHier, NULL)

SELECT @EmpCode = [Stores].[fnGenerateEmployeeCode](@FirstName, @LastName)
INSERT Stores.Employee (EmployeeCode, FirstName, LastName, OrgPath, Address1, StoreId, BloodGroup, IsManager, Designation)
VALUES (@EmpCode, @FirstName, @LastName, @newHier, @Address1, @StoreId, @Blood, @IsMan, @Designation)

