data.value:
-- database unit test for Stores.uspRemovePhoneNumber DECLARE @EntityPhoneId AS UNIQUEIDENTIFIER, @IsStore AS BIT, @PhoneDelXML AS XML(CONTENT [Stores].[DelPhoneXMLSchema]);
SET @IsStore = 0
SELECT @EntityPhoneId = EntityPhoneId FROM Stores.Employee WHERE LastName = 'TestLN' AND FirstName = 'TestFN'
SET @PhoneDelXML = ( SELECT MIN(p.PhoneId) AS '*' FROM Stores.Employee e INNER JOIN Stores.EmployeePhone ep ON e.EntityPhoneId = ep.EntityPhoneId INNER JOIN Stores.Phone p ON p.PhoneId = ep.PhoneId WHERE e.LastName = 'TestLN' AND e.FirstName = 'TestFN' AND IsPrimary = 0 FOR XML PATH('PhoneId'), ROOT('PhoneIdsToDelete'), TYPE )
EXECUTE [Stores].[uspRemovePhoneNumber] @EntityPhoneId, @IsStore, @PhoneDelXML;
SELECT COUNT(1) AS Cnt FROM Stores.Employee e INNER JOIN Stores.EmployeePhone ep ON e.EntityPhoneId = ep.EntityPhoneId INNER JOIN Stores.Phone p ON p.PhoneId = ep.PhoneId WHERE e.LastName = 'TestLN' AND e.FirstName = 'TestFN'

