-- database unit test for Stores.uspUpdateEmployeeDetails
DECLARE @RC AS INT, 
		@EmployeeCode AS CHAR (5),
		@EmpUpdXML AS XML(DOCUMENT [Stores].[UpdEmployeeSchema]), 
		@PhnUpdXML AS XML(DOCUMENT [Stores].[UpdPhoneXMLSchema]), 
		@TransferAllEmp AS BIT,
		@tobeparent hierarchyid;

SELECT @RC = 0,
       @EmployeeCode = EmployeeCode,       
       @PhnUpdXML = NULL,
       @TransferAllEmp = 0
FROM [Stores].[Employee]
WHERE [FirstName] = 'EMPFN1'
AND [LastName] = 'EMPLN1';

SELECT TOP 1 @tobeparent = OrgPath
FROM [Stores].[Employee]
WHERE [StoreId] = 2
AND IsManager = 1
AND Designation <> 'Owner'

SET @tobeparent = ISNULL(@tobeparent, 0x)

SET @EmpUpdXML = '
<EmployeeUpdate>
	<StoreId>2</StoreId>
</EmployeeUpdate>'

EXECUTE @RC = [Stores].[uspUpdateEmployeeDetails] @EmployeeCode, @EmpUpdXML, @PhnUpdXML, @TransferAllEmp;

SELECT StoreId, OrgPath.IsDescendantOf(@tobeparent) AS OrgPath
FROM [Stores].[Employee]
WHERE [EmployeeCode] = @EmployeeCode

SELECT @RC AS RC;

